import{_ as a,c as o,o as i,ae as s}from"./chunks/framework.tuuL-SzM.js";const e="/images/pin.jpg",r="/images/usb.jpg",l="/images/jst-sm-5pin.jpg",u=JSON.parse('{"title":"Варианты подключений к кондиционеру","description":"","frontmatter":{},"headers":[],"relativePath":"connect-to-air-conditioner.md","filePath":"connect-to-air-conditioner.md"}'),n={name:"connect-to-air-conditioner.md"};function c(p,t,d,m,g,h){return i(),o("div",null,t[0]||(t[0]=[s('<h1 id="варианты-подключении-к-кондиционеру" tabindex="-1">Варианты подключений к кондиционеру <a class="header-anchor" href="#варианты-подключении-к-кондиционеру" aria-label="Permalink to &quot;Варианты подключений к кондиционеру&quot;">​</a></h1><h3 id="подключение-к-модулю" tabindex="-1">Подключение к модулю <a class="header-anchor" href="#подключение-к-модулю" aria-label="Permalink to &quot;Подключение к модулю&quot;">​</a></h3><p>Модуль имеет 4 контакта для подключения:</p><ul><li><strong>VCC</strong>: питание модуля (работает при напряжении 5-19В)</li><li><strong>GND</strong>: земля</li><li><strong>RX</strong>: вход UART</li><li><strong>TX</strong>: выход UART</li></ul><img class="image" src="'+e+'" alt="Распиновка контактов"><div class="tip custom-block"><p class="custom-block-title">Примечание</p><p>Направление подключения RX и TX не критично, так как в модуле реализовано программное переключение направления UART. Это можно настроить в веб-интерфейсе модуля или в Home Assistant.</p></div><p>Если вы знаете распиновку контактов на вашем кондиционере, вы можете подключить модуль напрямую к соответствующим контактам, соблюдая только правильное подключение питания (VCC и GND).</p><p>Внутренний блок сплит-системы может иметь 5-проводное или 4-проводное подключение (псевдо-USB) к модулю Wi-Fi. Встречаются и другие виды подключения.</p><h3 id="_4-проводное-подключение-псевдо-usb" tabindex="-1">4-проводное подключение (псевдо-USB) <a class="header-anchor" href="#_4-проводное-подключение-псевдо-usb" aria-label="Permalink to &quot;4-проводное подключение (псевдо-USB)&quot;">​</a></h3><img class="image" src="'+r+'" alt="USB подключение"><p>Модуль специально разработан для подключения через USB-разъем кондиционера. От настоящего USB здесь только коннектор. По пинам в этом разъеме UART и питание.</p><h3 id="переходники" tabindex="-1">Переходники <a class="header-anchor" href="#переходники" aria-label="Permalink to &quot;Переходники&quot;">​</a></h3><p>Переходник с 5-пинового на плате кондиционера на USB можно приобрести на маркетплейсах или изготовить самостоятельно. Например может подойти <code>HOMMYN HCC-HI</code>. При самостоятельном изготовлении важно соблюдать правильную распиновку и полярность питания.</p><div class="tip custom-block"><p class="custom-block-title">Примечание</p><p>Полярность контактов RX и TX не важна, так как в модуле реализован программный переключатель направления UART.</p></div><h3 id="_5-проводное-подключение" tabindex="-1">5-проводное подключение <a class="header-anchor" href="#_5-проводное-подключение" aria-label="Permalink to &quot;5-проводное подключение&quot;">​</a></h3><p>Для 5-проводного подключения используется коннектор JST SM.</p><img class="image" src="'+l+'" alt="JST SM"><h4 id="распиновка" tabindex="-1">Распиновка <a class="header-anchor" href="#распиновка" aria-label="Permalink to &quot;Распиновка&quot;">​</a></h4><ul><li><strong>Красный</strong>: +12В..+14В постоянного тока.</li><li><strong>Черный</strong>: земля.</li><li><strong>Желтый</strong>: +5В постоянного тока. Не используется.</li><li><strong>Белый</strong>: TX кондиционера.</li><li><strong>Синий</strong>: RX кондиционера.</li></ul><div class="warning custom-block"><p class="custom-block-title">ВАЖНО</p><p>Перед подключением модуля обязательно проверьте уровни напряжения и полярность питания на контактах кондиционера! Неправильное подключение может привести к выходу из строя как модуля, так и кондиционера.</p></div>',20)]))}const b=a(n,[["render",c]]);export{u as __pageData,b as default};
